// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

contract DonationContract is Ownable {
    struct Donation {
        address donor; // Address of the donor
        uint256 amount; // Amount donated
        uint256 timestamp; // Timestamp of the donation
    }

    Donation[] public donations; // Array to store all donations
    mapping(address => uint256) public balances; // Mapping to track individual donor balances
    uint256 public totalDonations; // Variable to keep track of total donations

    event DonationReceived(address indexed donor, uint256 amount); // Event for tracking donations
    event FundsWithdrawn(address indexed charity, uint256 amount); // Event for tracking withdrawals

    // Constructor that initializes the Ownable contract
    constructor() Ownable(msg.sender) {
        totalDonations = 0; // Initialize total donations
    }

    // Function to donate funds to the contract
    function donate() external payable {
        require(msg.value > 0, "Donation must be greater than 0");

        donations.push(
            Donation({
                donor: msg.sender,
                amount: msg.value,
                timestamp: block.timestamp
            })
        );

        balances[msg.sender] += msg.value; // Update the donor's balance
        totalDonations += msg.value; // Update the total donations

        emit DonationReceived(msg.sender, msg.value); // Emit the donation event
    }

    // Function for the owner to withdraw all funds from the contract
    function withdraw() external onlyOwner {
        uint256 amount = address(this).balance; // Get the contract's balance
        require(amount > 0, "No funds available"); // Ensure there are funds to withdraw

        payable(owner()).transfer(amount); // Transfer the balance to the owner
        emit FundsWithdrawn(owner(), amount); // Emit the withdrawal event
    }

    // Function to get all donations
    function getDonations() external view returns (Donation[] memory) {
        return donations; // Return the array of donations
    }

    // Function to check the balance of a specific donor
    function getDonorBalance(address donor) external view returns (uint256) {
        return balances[donor]; // Return the balance of the specified donor
    }

    // Function to get the total donations
    function getTotalDonations() external view returns (uint256) {
        return totalDonations; // Return the total donations
    }
}
